import React from 'react'
import Main from './main'
import './style.css'
import { headerApi, blogApi, skillsApi, serviceApi, PortfolioApi,about } from './api'
const uniquePortfolio = [... new Set(PortfolioApi.map((curElem) => {
  return curElem.category;
})
)]
const App = () => {
  const [portfolioData, setportData] = React.useState(PortfolioApi)
  const [portfolioList] = React.useState(uniquePortfolio)
  const [headerData] = React.useState(headerApi)
  const [blogData] = React.useState(blogApi)
  const [serviceData] = React.useState(serviceApi)
  const [skillData] = React.useState(skillsApi)
  const filterPort = (category) => {
    const updateport = PortfolioApi.filter((curElem) => {
      return curElem.category === category;
    })
    setportData(updateport);
    if (category === 'all') {
      setportData(PortfolioApi);
    }
  }
  const filterSection = (section) =>{
    const updateSection = about.filter((curSection)=>{
      return curSection.category === section
    })
    setportData(updateSection);
  }
  return (
    <>
      <Main portfolioData={portfolioData} portfolioList={portfolioList} headerData={headerData} blogData={blogData} serviceData={serviceData} skillData={skillData} filterPort={filterPort} filterSection={filterSection}/>
    </>
  )
}

export default App
